import nav from ''
<template>
  <h1>{{ title }}</h1>

  <div>
    <input type="text"
    v-model="newTask"
    placeholder="Add a new task"
    >

    <button @click="addTask" :disabled="newTask.length < 1">
      Add Task
    </button>
  </div>

  <div v-if="newTask.length > 0">
    <h3>New Task preview</h3>
    <p>{{ newTask }}</p>
  </div>

  <ul>
    <li v-for="task in tasks" :key="task.id">
      {{ task.id }}. {{ task.name }}
      <div v-if="task.finished">
        <button>Delete Task</button>
      </div>
      <div v-else-if="task.edit">
        <button>Edit task</button>
      </div>
      <div v-else>
        <p>no Button</p>
      </div>
    </li>
  </ul>
</template>

<script>


export default { //สงสัย export deafault
  data(){
    return{
      title: 'My To-Do App',
      newTask:'',
      tasks: [
        {id: 1, name: 'Learn VUE js', finished: false},
        {id: 2, name: 'Build a Vue App', finished: false},
        {id: 3, name: 'Build Todo App', finished: false}
      ]
    }
  },
  methods:{
    addTask(){
      if(this.newTask.length < 1) return; //สงสัย การใช้ this

      this.tasks.push({
        id: this.tasks.length + 1,
        name: this.newTask,
        finished:false
      });

      this.newTask = '';
    }
  }
}
</script>

